import React from 'react'
import { useState } from 'react'

const AddTask = ({onAdd}) => {

    const [text,setText]=useState('')
    const [day,setDay]=useState('')
    const [reminder,setReminder]=useState(false)

    const onSubmit = async (e) => {
        e.preventDefault()

        if(!text){
            alert('Please add some task')
            return
        }

        const newTask = { text, day, reminder }

        // ðŸ‘‰ Send task to backend (added code)
        const res = await fetch("http://localhost:5000/tasks", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(newTask)
        })

        const data = await res.json()

        // ðŸ‘‰ Pass saved task to parent (stays same)
        onAdd(data)

        // reset inputs
        setText('')
        setDay('')
        setReminder(false)
    }

  return (
    <form className='add-form' onSubmit={onSubmit}>
        <div className='form-control'>
            <label>Task</label>
            <input type='text' placeholder='Add task' value={text} onChange={(e)=>{
                setText(e.target.value)
            }}></input>
        </div>
        <div className='form-control'>
            <label>Day & Time</label>
            <input type='text' placeholder='Add day and time' value={day} onChange={(e)=>{
                setDay(e.target.value)}}></input>
        </div>
        <div className='form-control form-control-check '>
            <label>Set Reminder</label>
            <input type='checkbox' checked={reminder} value={reminder} onChange={(e)=>{
                setReminder(e.currentTarget.checked)}}></input>
        </div>

      <input type='submit' value='Save Task' className='btn btn-block' />
    </form>
  )
}

export default AddTask
