const Task = ({ task, onDelete, onToggle }) => {
  return (
    <div
      className={`task ${task.reminder ? 'reminder' : ''}`}
      onDoubleClick={() => onToggle(task._id)}
    >
      <h3>
        {task.text}{' '}
        <button style={{ color: 'red' }} onClick={() => onDelete(task._id)}>
          X
        </button>
      </h3>
      <p>{task.day}</p>
    </div>
  );
};

export default Task;
